def print_grid():
    for row in grid:
        print("".join(row))

def neighbours(x, y): 
    return [grid[(x-1) % len(grid)][(y+1) % len(grid)], grid[x % len(grid)][(y+1) % len(grid)], grid[(x+1) % len(grid)][(y+1) % len(grid)],
            grid[(x-1) % len(grid)][y % len(grid)], grid[(x+1) % len(grid)][y % len(grid)],
            grid[(x-1) % len(grid)][(y-1) % len(grid)], grid[x % len(grid)][(y-1) % len(grid)], grid[(x+1) % len(grid)][(y-1) % len(grid)]
        ]


def switch_cell(live, cell_val):
    if live == 2 and cell_val == "*":
        return "*"
    elif live == 3 and cell_val == "*":
        return "*"
    elif live == 3 and cell_val == "-":
        return "*"
    else:
        return "-"

def rule_evol():
    for i, row in enumerate(grid, start=0):
        add_row = []
        for j, elt in enumerate(row, start=0):
            elm = switch_cell(neighbours(i, j).count("*"), grid[i][j])
            add_row.append(elm)
        
        updated_grid.append(add_row)

while True:
    updated_grid = []
    rule_evol()
    print_grid()
    print("")
    grid = updated_grid
    time.sleep(0.05)
